clientEvents.schema:
{
  "type": "object",
  "properties": {
    "deviceId": {
      "type": "string",
      "description": "Device ID this update belongs to.",
      "nullable": true
    },
    "currentState": ,
    "lastSeen": {
      "type": "string",
      "description": "The last time the device was seen online.",
      "format": "date-time"
    },
    "firmwareVersion": {
      "type": "string",
      "description": "The firmware version of the device.",
      "nullable": true
    },
    "lastError": {
      "type": "string",
      "description": "The last error message encountered by the device.",
      "nullable": true
    }
  },
  "additionalProperties": false,
  "description": "Represents a device state update for streaming.\r\nOptimized with Orleans serialization for maximum performance."
}

{
  "type": "object",
  "properties": {
    "machineId": {
      "type": "string",
      "description": "Identifier of the machine",
      "nullable": true
    },
    "machineName": {
      "type": "string",
      "description": "Name of the machine",
      "nullable": true
    },
    "availability": {
      "type": "number",
      "description": "Overall Equipment Effectiveness (OEE) percentage",
      "format": "double"
    },
    "availabilityVsLastPeriod": {
      "type": "number",
      "description": "Availability percentage compared to the last period. Percentage points difference.",
      "format": "double"
    },
    "performance": {
      "type": "number",
      "description": "Performance percentage",
      "format": "double"
    },
    "performanceVsLastPeriod": {
      "type": "number",
      "description": "Performance percentage compared to the last period. Percentage points difference.",
      "format": "double"
    },
    "quality": {
      "type": "number",
      "description": "Quality percentage",
      "format": "double"
    },
    "qualityVsLastPeriod": {
      "type": "number",
      "description": "Quality percentage compared to the last period. Percentage points difference.",
      "format": "double"
    },
    "oee": {
      "type": "number",
      "description": "Overall Equipment Effectiveness (OEE)",
      "format": "double"
    },
    "oeeVsLastPeriod": {
      "type": "number",
      "description": "OEE compared to the last period. Percentage points difference.",
      "format": "double"
    },
    "goodCount": {
      "type": "number",
      "description": "Count of good products produced",
      "format": "double"
    },
    "goodCountVsLastPeriod": {
      "type": "number",
      "description": "Good count compared to the last period. Difference in count.",
      "format": "double"
    },
    "totalCount": {
      "type": "number",
      "description": "Total count of products produced",
      "format": "double"
    },
    "totalCountVsLastPeriod": {
      "type": "number",
      "description": "Total count compared to the last period. Difference in count.",
      "format": "double"
    },
    "plannedProductionTime": {
      "type": "string",
      "description": "Duration in ISO 8601 format (e.g., PT1H30M for 1 hour 30 minutes, P1D for 1 day, PT45S for 45 seconds)",
      "format": "duration"
    },
    "runTime": {
      "type": "string",
      "description": "Duration in ISO 8601 format (e.g., PT1H30M for 1 hour 30 minutes, P1D for 1 day, PT45S for 45 seconds)",
      "format": "duration"
    },
    "downtime": {
      "type": "string",
      "description": "Duration in ISO 8601 format (e.g., PT1H30M for 1 hour 30 minutes, P1D for 1 day, PT45S for 45 seconds)",
      "format": "duration"
    },
    "speedLossTime": {
      "type": "string",
      "description": "Duration in ISO 8601 format (e.g., PT1H30M for 1 hour 30 minutes, P1D for 1 day, PT45S for 45 seconds)",
      "format": "duration"
    },
    "currentProductName": {
      "type": "string",
      "description": "Name of the current product running on the machine",
      "nullable": true
    }
  },
  "additionalProperties": false,
  "description": "Result of OEE calculation for a machine"
}

{
  "type": "object",
  "properties": {
    "startTime": {
      "type": "string",
      "description": "Start time of the runtime block.",
      "format": "date-time"
    },
    "endTime": {
      "type": "string",
      "description": "End time of the runtime block. Nullable to indicate ongoing blocks.",
      "format": "date-time",
      "nullable": true
    },
    "stopReasonId": {
      "type": "string",
      "description": "Identifier for the stop reason associated with this runtime block. ObjectId in string format. Empty string\r\nindicates normal operation.",
      "nullable": true
    },
    "name": {
      "type": "string",
      "description": "Name of the runtime block. Or stop reason name.",
      "nullable": true
    },
    "extraNote": {
      "type": "string",
      "description": "Color associated with the runtime block. Or stop reason color.",
      "nullable": true
    },
    "isUnplannedDowntime": {
      "type": "boolean",
      "description": "Indicates if the downtime was unplanned."
    },
    "state": 
  },
  "additionalProperties": false,
  "description": "Represents a runtime block with start and end times, stop reason, name, and color."
}

{
  "type": "object",
  "properties": {
    "sensorId": {
      "type": "string",
      "description": "Sensor ID this update belongs to.",
      "nullable": true
    },
    "value": {
      "type": "number",
      "description": "Sensor value.",
      "format": "double"
    },
    "timestamp": {
      "type": "string",
      "description": "Timestamp when the reading was taken.",
      "format": "date-time"
    }
  },
  "additionalProperties": false,
  "description": "Represents a sensor data update for streaming.\r\nOptimized with Orleans serialization for maximum performance."
}
